    function fix_select_box(){
        var target = $("form select");
        if(target){
            target.before("
");
            target.change(function(){
                $(this).prev().text($("option:selected",this).text());
            }).trigger("change");   
        }
    }

    function require_js( src, callback )
    {
        var script = document.createElement( 'script' );
        script.type = 'text/javascript';
        script.onload = function(){
            callback();
        };
        script.src = src;
        document.body.appendChild( script );
    }

function returnAttackParam (_obj){
	var _data = eval(_obj)
	var attackAreaParam = {
		"target": '',
		"detail":'',
		"patternName":'',
		"className":''
	};

	if(_data.is_healer == true){
		attackAreaParam.target = 'Heal';
		attackAreaParam.detail = 'Heals a single non-droid unit with the least % of HP left, positioned directly in front, to the sides, or behind the Healer unit.';
		attackAreaParam.patternName ='HEAL PATTERN';
		attackAreaParam.className = 'is_healer';
	}else if(_data.is_repairer == true){
		attackAreaParam.target = 'Repair';
		attackAreaParam.detail = 'Repairs a single droid/cyborg/vehicle unit with the least % of HP left, positioned directly in front, to the sides, or behind the Mechanic unit.';
		attackAreaParam.patternName ='HEAL PATTERN';
		attackAreaParam.className = 'is_repairer';
	}else if(_data.is_stun == true){
		attackAreaParam.target = 'Stun';
		attackAreaParam.detail = 'Stuns a non-vehicle enemy unit when the attack succeeds, causing the unit to become inactive during the next turn. No damage is inflicted.';
		attackAreaParam.patternName ='ATK PATTERN';
		attackAreaParam.className = 'is_stun';
	}else if(_data.is_stun_vehicle == true){
		attackAreaParam.target = 'Vehicle Stun';
		attackAreaParam.detail = 'Stuns an enemy Vehicle when the attack succeeds, causing the Vehicle to become inactive during the next turn. No damage is inflicted.';
		attackAreaParam.patternName ='ATK PATTERN';
		attackAreaParam.className = 'is_stun_vehicle';
	}else if(_data.is_giant_killing == true){
		attackAreaParam.target = 'Giant Slayer';
		attackAreaParam.detail = 'When attacking a Vehicle, this unit\'s ATK is calculated by dividing the opposing Vehicle\'s size (LxW) by 2 and then multiplying the result with the attacking unit\'s ATK.';
		attackAreaParam.patternName ='ATK PATTERN';
		if(_data.sub_type == 1){
			attackAreaParam.className = 'none';
		}
	}else if(_data.attack_area_type == 0){
		attackAreaParam.target = '1-on-1';
		attackAreaParam.detail = 'Attacks 1 unit within range in the front row.';
		attackAreaParam.patternName ='ATK PATTERN';
	}else if(_data.attack_area_type == 1){
		attackAreaParam.target = 'Sweep';
		attackAreaParam.detail = 'Attacks units in a single horizontal row.';
		attackAreaParam.patternName ='ATK PATTERN';
	}else if(_data.attack_area_type == 2){
		attackAreaParam.target = 'Pierce';
		attackAreaParam.detail = 'Attacks units in a single vertical column.';
		attackAreaParam.patternName ='ATK PATTERN';
	}else if(_data.attack_area_type == 3){
		attackAreaParam.target = 'Semi-Circle';
		attackAreaParam.detail = 'Attacks units within a semi-circle pattern around 1 targeted unit.';
		attackAreaParam.patternName ='ATK PATTERN';
	}else if(_data.attack_area_type == 4){
		attackAreaParam.target = 'Anti-Armor';
		attackAreaParam.detail = 'Attacks 1 Vehicle within range.';
		attackAreaParam.patternName ='ATK PATTERN';
	}else if(_data.attack_area_type == 5){
		attackAreaParam.target = 'Cross';
		attackAreaParam.detail = 'Attacks a unit on the middle row and units positioned directly in front, to the sides, and behind the attacked unit.';
		attackAreaParam.patternName ='ATK PATTERN';
	}else if(_data.attack_area_type == 6){
		attackAreaParam.target = 'Sniper';
		attackAreaParam.detail = 'Preferentially attacks units in the second row.';
		attackAreaParam.patternName ='ATK PATTERN';
	}else if(_data.attack_area_type == 7){
		attackAreaParam.target = 'Superlaser';
		attackAreaParam.detail = 'Attacks all units in the opposing Formation and inflicts a damage equal to 2% of this unit\'s ATK (non-integers are rounded down), regardless of the opposing unit\'s DEF.';
		attackAreaParam.patternName ='ATK PATTERN';
	}
	return attackAreaParam;
};



	var cardDetail = null;
	var CardDetail = function() {
		this.card   = null;
		this.series = null;
		this.doms   = null;
		this.elem   = $('#card-detal');
		this.list   = $('#card_list');
		this.zoom   = $('#card-zoom');

		this.setData = function(data,data2) {
			this.card = data;
			this.series = data2;
			return this;
		}

		this.show = function() {
			if (!this.doms) this.setDoms();
			var doms = this.doms;
			var card = this.card;
			var series = this.series;

			$('#evo_1').remove();
			$('#evo_2').remove();
			$('#evo_3').remove();
			$('#evo_4').remove();
			var evoLevelMin = null;
			$.each( series, function ( i, seriesData ) {
				var evoLevel	= Number(seriesData.evol_level);
				var evoId			= evoLevel + 1;
				evoLevelMin = ( evoLevelMin == null ) ? evoLevel : Math.min( evoLevelMin, evoLevel );
				switch ( evoLevel ) {
					case 0:
						$('#evolution').append('Non-Evolved');
						break;
					case 1:
						$('#evolution').append('Evo 1');
						break;
					case 2:
						$('#evolution').append('Evo 2');
						break;
					case 3:
						$('#evolution').append('Evo max');
						break;
				}
			});


			$('#evo_lve').change(function(){
				var evo_id = $("#evolution").val();
				var param = null;
				$.each( series, function( i, seriesData ) {
				  if ( seriesData.evol_level == evo_id ) {
				    param = seriesData;
				  }
				});
				doms.card_attack.html(param.attack);
				doms.card_defence.html(param.defence);
				doms.card_max_hp.html(param.max_hp);
				doms.card_price.html(param.price);
				doms.card_thumb.attr('src', param.thumb);
				doms.card_img.attr('src', param.img);
			});


			if (card.type == 1) {
				if (card.attribute == 1) {
					doms.card_side_icon.attr('class', 'side-icon').addClass('light').html('L');
				} else if (card.attribute == 2) {
					doms.card_side_icon.attr('class', 'side-icon').addClass('dark').html('D');
				} else {
					doms.card_side_icon.attr('class', 'side-icon').addClass('neutral').html('N');
				}
				doms.card_side_icon.show();

				doms.rarity.show();
				doms.card_rarity.html(card.rarity_name).show();
			} else {
				doms.rarity.hide();
				doms.card_side_icon.hide();
			}

			if (card.type == 3) {
				doms.max_level.hide();
			}else{
				doms.max_level.show();
			}


			if (card.type == 2) {
				doms.slot.show();
				doms.card_slot_x.html(card.slot_x).show();
				doms.card_slot_y.html(card.slot_y).show();
				if(card.blue_print_thumb.length != 0){
					doms.bp_vehicle.show();
				}else{

					doms.zoom_bp_thumb.hide();
					doms.bp_vehicle.hide();
				}
			} else {
				doms.bp_vehicle.hide();
				doms.slot.hide();
			}

			doms.zoom_bp_thumb.hide();

			if (card.skill_id > 0) {

				doms.skill_name.show();
				doms.card_skill_name.html(card.skill_name).show();

				/*doms.skill_level.show();
				skill_level_txt.html(card.skill_level).show();*/

				doms.skill_info.show();
				doms.card_skill_info.html(card.skill_comment).show();
			} else {

				doms.skill_name.hide();
				doms.card_skill_name.hide();


				doms.skill_info.hide();
				doms.card_skill_info.hide();
			}


			var defaultCardData = ( card.type == 1 ) ? series[0] : card;
			doms.card_thumb.attr('src', defaultCardData.thumb);
			//ipad ipad:L(L) :M
							doms.card_img.attr('src', 'http://amw.konaminet.jp/amw/naboo/img/'+defaultCardData.thumb+'_m.png');
						doms.card_price.html(defaultCardData.price);
			doms.card_attack.html(defaultCardData.attack);
			doms.card_defence.html(defaultCardData.defence);
			doms.card_max_hp.html(defaultCardData.max_hp);
			if(defaultCardData.raceData != 0){
	            var race_html = "";
	            for( i=0; i<defaultCardData.raceData.length; i++){
				    race_html += '';
	            }
				doms.card_race.html(race_html);
				doms.card_race.parent(".param").show();
			}else{
				doms.card_race.parent(".param").hide();
			}
			doms.card_id.html(card.card_id);
			doms.card_name.html(card.name);
			if (card.name.length > 30) {
				$('#detail_name').addClass('fontS');
			} else {
				$('#detail_name').removeClass('fontS');
			}
			doms.card_range.html(card.range);
			doms.detail_num_attacks.html(card.per_text);
			if (card.sub_type == 0) {
				doms.card_range.parent(".param").show();
				$("#detail_num_attacks").parent(".param").show();
				$("#detail_attack_wrapper").show();
			}else{
				doms.card_range.parent(".param").hide();
				if(card.attack_time == 0){
					$("#detail_num_attacks").parent(".param").hide();
				}else{
					if(card.attack_time > 0){
						$("#detail_num_attacks").html("Number of Attacks Decreases");
					}else{
						$("#detail_num_attacks").html("Number of Attacks Increases");
					}
					$("#detail_num_attacks").parent(".param").show();
				}
				if(card.attack_area_type != 0 || card.is_stun || card.is_stun_vehicle || card.is_giant_killing){
					$("#detail_attack_wrapper").show();
				}else{
					$("#detail_attack_wrapper").hide();
				}
			}
			doms.card_cost.html(card.cost);
			doms.card_max_level.html(card.max_level);
			doms.accuracy.html(card.accuracy);
			doms.avoid.html(card.avoid);
			doms.card_comment.html(card.comment);

			if( card.is_healer){
				doms.caps_card_attack.html("HEA: ");
			} else if(card.is_repairer){
				doms.caps_card_attack.html("REP: ");
			} else {
				doms.caps_card_attack.html("ATK: ");
				doms.detail_attack_area_pattern.html("ATK PATTERN: ");
				doms.detail_attack_area.attr('class','attack_area' + card.attack_area_type + card.attack_area + ' slot_x0' + card.slot_x);
			}

			doms.wish_link.unbind().click(function() {
				ajax_load(card.wish_link, {}, function(){});
			});


			var attackParam = returnAttackParam(card);
			$("#detail_attack_area_txt").html(attackParam.target);
			$("#detail_attack_area_detail").html(attackParam.detail);
			$("#detail_attack_area_pattern").html(attackParam.patternName+": ");
			$("#detail_attack_area").addClass(attackParam.className);


			$("#bp_trigger").click(function(){
				$(this).hide();
				$("#bp_vehicle").show();
				$('#zoom_img_thumb').hide();
				$('#zoom_bp_thumb').show();
				var imgHtml = $('').attr('src', card.blue_print_thumb)
										.attr('id', 'zoom_bp')
										.attr('class', "thumb bluebpirnt")
										.attr('style', 'display:none;')
										.attr('width', 285);
				$('#zoom_bp_wrapper').html(imgHtml);
				$('.now_loading').show();
				$('#zoom_bp').load(function() {
									$(this).show();
									$('.shadow','#zoom_bp_thumb').show();
									$('.now_loading').hide();
								});
			});
			$("#bp_vehicle").click(function() {
				$(this).hide();
				$("#bp_trigger").show();
				$('#zoom_img_thumb').show();
				$('#zoom_bp_thumb').hide();
			});



			this.list.hide();
			this.elem.show();
			sendMessageToApp('button.seturl', {'url':'javascript:close()'});




			sendMessageToApp('button.seturl', {'button':'back', 'url':'javascript:cardDetail.hide()'});
			return this;
		}

		this.hide = function() {
