		}
	}

	var LibraryControl = function() {
		this.menu_open		= $('#menu_open');
		this.menu_close		= $('#menu_close');
		this.sort_selector	= $('#sort_selector');
		this.lib_list		= $('#card_elems');
		this.seach_setting	= $('#seach_setting');

		this.showLib = function() {
			this.sort_selector.attr('disabled', false).css('opacity', '1');
			this.seach_setting.hide();
			this.lib_list.show();
			this.menu_close.hide();
			this.menu_open.show();
		}

		this.showSetting = function() {
			this.sort_selector.attr('disabled', true).css('opacity', '0.4');
			this.lib_list.hide();
			this.seach_setting.show();
			this.menu_open.hide();
			this.menu_close.show();
		}

		this.init = function() {
			var self = this;
			this.showLib();
			this.menu_open.click(function() {
				self.showSetting();
			});
			this.menu_close.click(function() {
				self.showLib();
			});

			$('#card-zoom').implant_dialog({closer:'#setting_closer', "scroll":true, no_close:true});
			$('#save_setting').click(function() {
				var pub = $(this).siblings('select').val();
				ajax({
					'url': 'http://amw.konaminet.jp/amw/naboo/book/config/edit?PHPSESSID=28HF7D8Q8M4M6FB5FMB4KNHD&opensocial_viewer_id=521175497&u=1428073892',
					'loading_icon': true,
					'param': {'pub': pub},
					'callback': function(data) {
						$('#card-zoom').close_implant_dialog();
						if (data.err) {
							$("#error-dialog").call_implant_dialog();
						} else {
							var dialog = $("#complete-dialog");
							dialog.find('div.box-area span').hide();
							if (pub == "1") {
								$('#dialog_message_allies').show();
							} else if (pub == "2") {
								$('#dialog_message_public').show();
							} else {
								$('#dialog_message_private').show();
							}

							dialog.call_implant_dialog();
						}
						ajax_load_lock = false;
					},
					'error': function() {
						$('#card-zoom').close_implant_dialog();
						$("#error-dialog").call_implant_dialog();
					}
				});
			});
			$('#setting_button').click(function() {
				$("#card-detail").hide();
				$("#bp_trigger").show();
				$("#bp_vehicle").hide();
				$("#zoom_bp_thumb").hide();
				$("#zoom_img_thumb").show();
				$("#zoom_thumb, .shadow").show();
				//ipad ipad:LL :L
									var imgSize = "http://amw.konaminet.jp/amw/naboo/img/" + $('#card_thumb').attr('src')+"_l.png";
								var imgHtml = $('').attr('src', imgSize).attr('id', 'zoom_thumb')
										 .attr('class', "thumb card")
										 .attr('height', "320");
				$('#zoom_thumb_wrapper').html(imgHtml);
				$('#zoom_thumb, .shadow').hide();
				$('.now_loading').show();
				sendMessageToApp("scroll.top");
				$('#card-zoom').call_implant_dialog();
				$('#zoom_thumb').load(function() {
									$(this).show();
									$('.shadow').show();
									$('.now_loading').hide();
								});
			});
		}
	}

	$(document).ready(function() {
		var checkboxs = $('input:checkbox').change(function() {
			$(this).parent().siblings('.check-box').toggleClass('on').toggleClass('off');
		});

		var ctrl = new LibraryControl();
		cardDetail = new CardDetail();

		var postManager = function() {
			this.post = function() {
				var form = $(this).parents("form");
				ctrl.sort_selector.attr('disabled', false).css('opacity', '1');
				var rel = form.serializeArray();
				ctrl.sort_selector.attr('disabled', true).css('opacity', '0.4');
				var checks = {};
				$.each(rel, function(i, v) {
					if (checks[v.name]) {
						checks[v.name] += ','+v.value;
					} else {
						checks[v.name] = v.value;
					}
				});
				ajax_load_lock = false;
				ajax_load(form.attr("action"), checks, function(){});
			}
		}

		var post = new postManager();
		ctrl.init();

		$("form :button" + ".submit").bind("click", post.post);
		ctrl.sort_selector.bind("change", post.post);

		$("#card-list a").unbind().ajax_link({
			"callback": function(data) {
				if (data.err == 0) {
					cardDetail.setData(data.card,data.series_list).show();
				} else {
					$("#error-dialog").call_implant_dialog();
				}
			}
		});

		$("#complete-dialog").implant_dialog({closer:'#complete_closer'});
		$("#error-dialog").implant_dialog({close:'#error_closer'});

	});

function delayFilter(){
	var that = document.getElementById("attribute_filter_selector");
	setTimeout(function(){ajax_load(that.options[that.selectedIndex].value, null, function(){});}, 100);
}

